        "%context%": true,
        "index": "participation",
        "body": {
          "size": 0,
          "aggs": {
            "buckets": {
              "composite": {
                "sources": [
                  {"key": {"terms": {"field": "radar.key.keyword"}}},
                  {"category": {"terms": {"field": "ideology.keyword"}}}
